import React, { useState, useEffect } from 'react';
import './App.css';

function App() {

    return (
        <div className="App" style={{ backgroundColor: 'transparent' }}>
            <header className="App-header">
                <div src="path/to/your/image.jpg" alt="Circular Image" class="circular-image"></div>
                <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'start' }}>

                    <h1 style={{ color: "#ffffff", fontFamily: "cursive" }}>Hello I'm</h1>
                    <h1 style={{ color: "#00C08D", fontFamily: "cursive", marginLeft: 20 }}>Prathamesh Sawardekar</h1>
                </div>

                <p>
                    <TypingAnimation text="Welcome to My Portfolio, Let me showcase you My Journey in Software Development, UI/UX Design, and Innovation" speed={100} />
                </p>
            </header>
        </div>
    );
}

const TypingAnimation = ({ text, speed }) => {
    const [displayedText, setDisplayedText] = useState("");
    const [isTyping, setIsTyping] = useState(true);

    useEffect(() => {
        let currentIndex = -1;
        let interval;

        const typeText = () => {
            if (currentIndex < text.length) {
                setDisplayedText((prev) => prev + text[currentIndex]);
                currentIndex++;
            } else {
                clearInterval(interval);
                setTimeout(() => {
                    // Clear the text and restart typing animation after a short delay
                    setDisplayedText("");
                    currentIndex = -1;
                    interval = setInterval(typeText, speed);
                }, 3000); // Pause for 1 second before restarting
            }
        };

        if (isTyping) {
            interval = setInterval(typeText, speed);
        }

        return () => clearInterval(interval); // Clean up the interval on component unmount
    }, [text, speed, isTyping]);

    return <span style={{ marginLeft: "25vh", marginRight: "25vh" }}>{displayedText}</span>;
};

export default App;
